head.script:
        var menuId ='TSMMMM000SD000TOTSE';
        $.extend({
        	getUrlVars: function(){
        		var vars = [], hash;
        		var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
        		for(var i = 0; i < hashes.length; i++)
        		{
        			hash = hashes[i].split('=');
        			vars.push(hash[0]); //key값 insert
        			vars[hash[0]] = hash[1]; //vars배열 안에 hash배열안에 값이 들어감
        		}
        		return vars; //vars에는 url 주소 값을 key, value로 값을 가지고 가게됨
        	},
        	getUrlVar: function(name){
        		return $.getUrlVars()[name]; //알아서 key값 찾음
        	}
        }); 

        var search = {};
		var btnFlag = WebSquare.net.getParameter("btn");

        search.url = "/websquare/websquare.jsp?w2xPath=/ui/portal/search/sd001_ts001.xml&query=";

        search.init = function(qry) {
        	if(qry == ""){
        		alert("검색어를 입력하세요");
        		return;
        	}

        	var qryTxt = decodeURIComponent(qry);

        	search_word.setLabel("'" + qryTxt + "'");
        	search_count.setLabel("0");

        	ajaxLib.srhAJAX(qry);

        	search.getResult_stat(qry);
        	search.getResult_dset(qry);
        	search.getResult_menu(qry);
        	search.getResult_bbs(qry);
        	search.getResult_wdic(qry);
        	search.getResult_fest(qry);            	
			unifiedSearchWord.setValue(decodeURI(keyVal));
        };



        search.setSearchEnv = function(val, fld) {
        	var env = "Parser="
        			+ "&ColList=" + val
        			+ "&PageNum=1"
        			+ "&PageMax=3"
        			+ "&FieldList=" + fld
        			+ "&SortOrder="
        			+ "&User=";

        	return env;
        };

        search.getResult_stat = function(val) {
        	//ajaxLib.srhAJAX(val);     
        	var url = "/mir/search.jsp?"
        			+ search.setSearchEnv("stat", "url,@title,@_doc_summary,@cate_fullnm,zdate")
        			+ "&Query=" + val
        			+ "&QY=" + val
        			+ "&callback=?";

        	$.getJSON(url, function(data){
        		var json = data;
        		var col = json.stat;

        		var count = parseInt(search_count.getLabel()) + parseInt(col.ResultCount);        		
        		srch_all.setLabel("전체보기("+String(count)+")");
        		search_count.setLabel(String(count));
        		stat_count.setLabel(col.ResultCount);        		
        		srch_n1.setLabel("재정전문통계("+col.ResultCount+")");

        		if(parseInt(col.ResultCount) > 3){
        			ac_stat_more.bind("onclick", function(){pFn.srch_n1_onclick();});
        			stat_more.visible( true );
        		}
        		else if(parseInt(col.ResultCount)==0){
        			result_stat1.hide();
        			result_stat2.hide();
        			srch_n1_li.hide();
        		}        		
        		else{
        			stat_more.visible( false );
        		}

        		if(parseInt(col.ResultCount) > 0) srch_n1_li.show();

        		gn_stat.removeAll();
        		$.each(col.Results, function(keyVal, state){
        			var obj = state;
        			var wdate = obj.zdate.split(' ');

        			gn_stat.insert();
        			gn_stat.getChild(keyVal, "stat_title").setValue(obj['@title']);
        			gn_stat.getChild(keyVal, "stat_title").setHref(obj.url);
        			gn_stat.getChild(keyVal, "stat_summary").setValue(obj['@_doc_summary']);
        			gn_stat.getChild(keyVal, "stat_category").setValue(obj['@cate_fullnm']);
        			//gn_stat.getChild(keyVal, "stat_date").setValue(wdate[0]);
        		});        		
        	});
        };

        search.getResult_dset = function(val) {
        	//ajaxLib.srhAJAX(val); 
        	var url = "/mir/search.jsp?"
        			+ search.setSearchEnv("dset", "_miracskey,@title,@_doc_summary,@cate_fullnm,zdate,@cate_nm,open_srv,znum,hold_year")
        			+ "&Query=" + val
        			+ "&QY=" + val
        			+ "&callback=?";

        	$.getJSON(url, function(data){
        		var json = data;
        		var col = json.dset;

       			var count = parseInt(search_count.getLabel()) + parseInt(col.ResultCount);
       			srch_all.setLabel("전체보기("+String(count)+")");
        		search_count.setLabel(String(count));
        		dset_count.setLabel(col.ResultCount);        		
        		srch_n2.setLabel("데이터셋("+col.ResultCount+")");

        		if(parseInt(col.ResultCount) > 3){
        			ac_dset_more.bind("onclick", function(){pFn.srch_n2_onclick();});
        			dset_more.visible( true );
        		}
        		else if(parseInt(col.ResultCount)==0){
					result_dset1.hide();
					result_dset2.hide();
					srch_n2_li.hide();        		
        		}
        		else{
        			dset_more.visible( false );
        		}

        		if(parseInt(col.ResultCount) > 0) srch_n2_li.show();

        		gn_dset.removeAll();
        		$.each(col.Results, function(keyVal, state){
        			var obj = state;

   					var dset_url = "/portal/service/openInfPage.do"
      								+ "?infId=" + obj._miracskey
       								+ "&srvCd=";

        			gn_dset.insert();

        			if(obj['@cate_fullnm'] != ""){
        				var ico_class;
        				var ico_val = obj['@cate_fullnm'].split('>');

        				if(ico_val[0] == "지방자치단체" || ico_val[0] == "통합공시") ico_class = "ico_org01";
        				else if(ico_val[0] == "지방공기업") ico_class = "ico_org02";
        				else if(ico_val[0] == "지방교육기관") ico_class = "ico_org03";
        				else ico_class = "ico_org04";

        				gn_dset.getChild(keyVal, "dset_ico").addClass(ico_class);
        			}

        			gn_dset.getChild(keyVal, "dset_title").setValue(obj['@title']);
        			gn_dset.getChild(keyVal, "dset_title").setHref(dset_url);
        			gn_dset.getChild(keyVal, "dset_summary").setValue(obj['@_doc_summary']);
        			gn_dset.getChild(keyVal, "dset_summary").setHref(dset_url);

					gn_dset.getChild(keyVal, "type_sheet").visible( false );
					gn_dset.getChild(keyVal, "type_api").visible( false );
					gn_dset.getChild(keyVal, "type_chart").visible( false );
					gn_dset.getChild(keyVal, "type_file").visible( false );
					gn_dset.getChild(keyVal, "type_map").visible( false );
					gn_dset.getChild(keyVal, "type_link").visible( false );
					gn_dset.getChild(keyVal, "type_time").visible( false );

        			if(obj.open_srv != ""){
        				var ico_types = obj.open_srv.split(',');

        				for(var i in ico_types){
        					if(ico_types[i].charAt(0) == 'S'){ 
        						gn_dset.getChild(keyVal, "type_sheet").visible( true );
        						gn_dset.getChild(keyVal, "ac_sheet").setHref(dset_url + "S");
        					}
        					else if(ico_types[i].charAt(0) == 'A'){
        						gn_dset.getChild(keyVal, "type_api").visible( true );
        						gn_dset.getChild(keyVal, "ac_api").setHref(dset_url + "A");
        					}
        					else if(ico_types[i].charAt(0) == 'C'){
        						gn_dset.getChild(keyVal, "type_chart").visible( true );
        						gn_dset.getChild(keyVal, "ac_chart").setHref(dset_url + "C");
        					}
        					else if(ico_types[i].charAt(0) == 'F'){
        						gn_dset.getChild(keyVal, "type_file").visible( true );
        						gn_dset.getChild(keyVal, "ac_file").setHref(dset_url + "F");
        					}
        					else if(ico_types[i].charAt(0) == 'M'){
        						gn_dset.getChild(keyVal, "type_map").visible( true );
        						gn_dset.getChild(keyVal, "ac_map").setHref(dset_url + "M");
        					}
        					else if(ico_types[i].charAt(0) == 'L'){
        						gn_dset.getChild(keyVal, "type_link").visible( true );
        						gn_dset.getChild(keyVal, "ac_link").setHref(dset_url + "L");
        					}
        					else if(ico_types[i].charAt(0) == 'T'){
        						gn_dset.getChild(keyVal, "type_time").visible( true );
        						gn_dset.getChild(keyVal, "ac_time").setHref(dset_url + "T");
        					}
        				}
        			}

        			gn_dset.getChild(keyVal, "dset_num").setValue(obj.znum);
        			//gn_dset.getChild(keyVal, "dset_date").setValue(obj.zdate);
        			gn_dset.getChild(keyVal, "dset_holdYear").setValue(obj.hold_year);
        			gn_dset.getChild(keyVal, "dset_catenm").setValue(obj['@cate_nm']);
        		});
        	});
        };

        search.getResult_menu = function(val) {
        	//ajaxLib.srhAJAX(val); 
        	var url = "/mir/search.jsp?"
        			+ search.setSearchEnv("menu", "@menu_nm,menu_url,@menu_desc")
        			+ "&Query=" + val        			
        			+ "&QY=" + val
        			+ "&callback=?";

        	$.getJSON(url, function(data){
        		var json = data;
        		var col = json.menu;

        		var count = parseInt(search_count.getLabel()) + parseInt(col.ResultCount);        		
        		srch_all.setLabel("전체보기("+String(count)+")");
        		search_count.setLabel(String(count));
        		menu_count.setLabel(col.ResultCount);
        		srch_n3.setLabel("알기 쉬운 지방재정("+col.ResultCount+")"); 

        		if(parseInt(col.ResultCount) > 3){
        			ac_menu_more.bind("onclick", function(){pFn.srch_n3_onclick();});
        			menu_more.visible( true );
        		}
        		else if(parseInt(col.ResultCount)==0){
        			result_menu1.hide();
        			result_menu2.hide();
        			srch_n3_li.hide();
        		}        		
        		else{
        			menu_more.visible( false );
        		}

        		if(parseInt(col.ResultCount) > 0) srch_n3_li.show();

        		gn_menu.removeAll();
        		$.each(col.Results, function(keyVal, state){
        			var obj = state;

        			gn_menu.insert();
        			gn_menu.getChild(keyVal, "menu_title").setValue(obj['@menu_nm']);
        			gn_menu.getChild(keyVal, "menu_title").setHref(obj.menu_url);
        			gn_menu.getChild(keyVal, "menu_summary").setValue(obj['@menu_desc']);
        		});
        	});
        };


        search.getResult_bbs = function(val) {
        	//ajaxLib.srhAJAX(val); 
        	var url = "/mir/search.jsp?"
        			+ search.setSearchEnv("bbs", "_miracskey,@ztitle,@_doc_summary,@user_nm,zdate,file_seq,view_file_nm,list_sub_cd,file_size")
        			+ "&Query=" + val
        			+ "&QY=" + val
        			+ "&callback=?";

        	$.getJSON(url, function(data){
        		var json = data;
        		var col = json.bbs;

        		var count = parseInt(search_count.getLabel()) + parseInt(col.ResultCount);
        		search_count.setLabel(String(count));
        		srch_all.setLabel("전체보기("+String(count)+")");
        		bbs_count.setLabel(col.ResultCount);
        		srch_n4.setLabel("통합자료실("+col.ResultCount+")");    

        		if(parseInt(col.ResultCount) >= 3){
        			ac_bbs_more.bind("onclick", function(){pFn.srch_n4_onclick();});
        			bbs_more.visible( true );        			
        		}
        		else if(parseInt(col.ResultCount)==0){
        			result_bbs1.hide();
        			result_bbs2.hide();
        			srch_n4_li.hide();
        		}        		
        		else{
        			bbs_more.visible( false );
        		}

        		if(parseInt(col.ResultCount) > 0) srch_n4_li.show();

        		gn_bbs.removeAll();
        		$.each(col.Results, function(keyVal, state){
        			var obj = state;
        			var cd_val = "";

					if( obj.list_sub_cd != "FLS401" 
						|| obj.list_sub_cd != "FLS402"
						|| obj.list_sub_cd != "FLS403"
						|| obj.list_sub_cd != "FLS404"
						|| obj.list_sub_cd != "FLS418"
						) cd_val = obj.list_sub_cd;

        			var bbs_url = "/portal/bbs/secretYnChk.do"
        						+ "?bbsCd=FSL1004"
        						+ "&code=fsl"
        						+ "&leftCd=1"
        						+ "&ansTag=N"
        						+ "&subCd=" + cd_val
        						+ "&seq=" + obj._miracskey;

        			gn_bbs.insert();
        			gn_bbs.getChild(keyVal, "bbs_title").setValue(obj['@ztitle']);
        			gn_bbs.getChild(keyVal, "bbs_title").setHref(bbs_url);
        			gn_bbs.getChild(keyVal, "bbs_summary").setValue(obj['@_doc_summary']);
        			gn_bbs.getChild(keyVal, "bbs_author").setValue(obj['@user_nm']);
        		//	gn_bbs.getChild(keyVal, "bbs_date").setValue(obj.zdate);

        			if(obj.view_file_nm != ""){
        				var attach = obj.view_file_nm.split(',');
        				var attach_size = obj.file_size.split(',');
        				var attach_seq = obj.file_seq.split(',');

        				var attach_list = gn_bbs.getChild(keyVal, "gn_attach");
        				attach_list.removeAll();
        				for(var i in attach){
        					var dn_url = "/portal/bbs/fileDownload.do"
        								+ "?downCd=B"
        								+ "&seq=" + obj._miracskey
        								+ "&fileSeq=" + attach_seq[i] 
        								+ "&fileSize=" + attach_size[i]
        								+ "&fileGubun=" + "";

        					attach_list.insert();

        					if(i == 0){
        						attach_list.getChild(i, "gn_in_span").setValue("첨부파일 : ");
        					}

        					attach_list.getChild(i, "ac_attach").setValue(attach[i]);
        					attach_list.getChild(i, "ac_attach").setHref(dn_url);
        				}
        			}
        			else{
        				var attach_list = gn_bbs.getChild(keyVal, "gn_attach");
        				attach_list.visible( false );
        			}
        		});
        	});
        };

        search.getResult_wdic = function(val) {
        	//ajaxLib.srhAJAX(val); 
        	var url = "/mir/search.jsp?"
        			+ search.setSearchEnv("wdic", "bbs_tit,@bbs_tit,@bbs_cont")
        			+ "&Query=" + val        			
        			+ "&QY=" + val
        			+ "&callback=?";

        	$.getJSON(url, function(data){
        		var json = data;
        		var col = json.wdic;

        		var count = parseInt(search_count.getLabel()) + parseInt(col.ResultCount);
        		search_count.setLabel(String(count));
        		srch_all.setLabel("전체보기("+String(count)+")");
        		wdic_count.setLabel(col.ResultCount);
        		srch_n5.setLabel("재정용어("+col.ResultCount+")"); 

        		if(parseInt(col.ResultCount) > 3){
        			ac_wdic_more.bind("onclick", function(){pFn.srch_n5_onclick();});
        			wdic_more.visible( true );
        		}
        		else if(parseInt(col.ResultCount)==0){
        			result_wdic1.hide();
        			result_wdic2.hide();
        			srch_n5_li.hide();
        		}        		
        		else{
        			wdic_more.visible( false );
        		}

        		if(parseInt(col.ResultCount) > 0) srch_n5_li.show();

        		gn_wdic.removeAll();
        		$.each(col.Results, function(keyVal, state){
        			var obj = state;

        			var sword = obj.bbs_tit.split('(');

        			var wdic_url = "/portal/bbs/bbsListPage.do"
        						+ "?bbsCd=FSL1002"
        						+ "&code=fsl"
        						+ "&leftCd=6"
        						+ "&subCd="
        						+ "&searchWd=" + encodeURI(sword[0]);

					gn_wdic.insert();
					gn_wdic.getChild(keyVal, "wdic_title").setValue(obj['@bbs_tit']);
					//gn_wdic.getChild(keyVal, "wdic_title").setHref(wdic_url);
					gn_wdic.getChild(keyVal, "wdic_summary").setValue(obj['@bbs_cont']);

					$("#"+gn_wdic.getChild(keyVal, "wdic_title").id).click(function(){
						window.open(wdic_url, "bbsDic", "width=720px, height=760px, menubar=no, status=no, toolbar=no, location=no, scrollbars=no, resizable=no").focus();
						return false;
					});

        		});
        	});
        };

