        search.getResult_fest = function(val) {        	
        	//ajaxLib.srhAJAX(val); 
        	var url = "/mir/search.jsp?"
        			+ search.setSearchEnv("fest", "accnut_year,sfrnd_code,high_cd,@sfrnd_nm_korean,prmpc_accnut_info_code,@event_fstvl_nm,@bsns_purps,url,@type")
        			+ "&Query=" + val        			
        			+ "&QY=" + val
        			+ "&callback=?";

        	$.getJSON(url, function(data){
        		var json = data;
        		var col = json.fest;

        		var count = parseInt(search_count.getLabel()) + parseInt(col.ResultCount);
        		srch_all.setLabel("전체보기("+String(count)+")");
        		search_count.setLabel(String(count));
        		fest_count.setLabel(col.ResultCount);
        		srch_n7.setLabel("행사축제("+col.ResultCount+")");  

        		if(parseInt(col.ResultCount) > 3){
        			ac_fest_more.bind("onclick", function(){pFn.srch_n7_onclick();});
        			fest_more.visible( true );
        		}
        		else if(parseInt(col.ResultCount)==0){
        			result_fest1.hide();
        			result_fest2.hide();
        			srch_n7_li.hide();
        		}        		
        		else{
        			fest_more.visible( false );
        		}

        		if(parseInt(col.ResultCount) > 0) srch_n7_li.show();

        		gn_fest.removeAll();
        		$.each(col.Results, function(keyVal, fest){
        			var obj = fest;

        			var fest_url = "/websquare/websquare.jsp?w2xPath=/ui/portal/gongsi/item/sd002_tg002_A018_01.xml"        						
        						+ "&year=" + obj.accnut_year
        						+ "&type=" + obj.sfrnd_code
        						+ "&kind=" + obj.prmpc_accnut_info_code;

        			gn_fest.insert();
        			gn_fest.getChild(keyVal, "fest_title").setValue(obj['@event_fstvl_nm']);
        			//gn_fest.getChild(keyVal, "fest_title").setHref(fest_url);
        			gn_fest.getChild(keyVal, "fest_title").setHref(obj.url);
        			gn_fest.getChild(keyVal, "fest_summary").setValue(obj['@bsns_purps']);
        			gn_fest.getChild(keyVal, "fest_type").setValue(obj['@type']); 
        			gn_fest.getChild(keyVal, "fest_sfrnd_nm_korean").setValue(obj['@sfrnd_nm_korean']);         			
        		});
        	});
        };

