		pFn.searchBtn1_onclick = function(){

			if(resultAll.getValue() == "" && resultOne.getValue() == ""){
				alert("검색어를 입력하세요");
			}
			else{
				//검색어 필터링 추가	
					$.ajax({
						url: "/portal/search/prohibitCheck.do",
						data: "unifiedSearchWord=" + $("#resultAll").val() +"&unifiedSearchWord=" +$("#resultOne").val(),
						type: "post",
						dataType: "json",
						async: false,
						success: prohbCallbackAND
					});
			}
		};

		function prohbCallbackAND(data){

		//검색어 필터링
			if(data.result1 > 0 || data.result2 > 0){
				if(data.result1 >0){
					alert("『" + $("#resultAll").val() + "』은(는) 검색하실 수 없는 검색어 입니다.");
				}
				else if(data.result2 > 0){
					alert("『" + $("#resultOne").val() + "』은(는) 검색하실 수 없는 검색어 입니다.");
				}
				$("#resultAll").focus();
			}
			else{
				var txt1 = resultAll.getValue().replaceAll(" ", "*");
				var txt2 = resultOne.getValue().replaceAll(" ", "+");

				if(resultAll.getValue() != "" && resultOne.getValue() != "") {
					var search_url = search.url + txt1 + "+" + txt2;
				}
				else if(resultAll.getValue() != ""){
					var search_url = search.url + txt1;				
				}
				else{
					var search_url = search.url + txt2;
				}
				location.href = search_url;
			}

		}
