head.script:

        		//var menuId = WebSquare.net.getParameter( "menuId" );
				var menuId='TMMMMM001SD006TH024_01';

				//Websquare 실행을 위한 선언
				pFn.execSubmission = function(submissionID) {

					WebSquare.ModelUtil.executeSubmission(submissionID);

				};


				pFn.getMapCodeInfo = function() {
					pFn.execSubmission("getMapCode");
				};

				pFn.fn_CreateSubChart01 = function(dataObj){
					txt_chart1_title.setValue( dm_search.get("sigunguNm") + " 지방보조금 현황" );

					subChart01.setJSONData(dataObj);
					setCmmThemOpt(subChart01, "Column", "MIL", false,0,0);
					subChart01.draw();

					test();

					subChart01.setAccessibility(0);
					subChart01.setAccessibility(1);
					setChartLabelInit();

				};

				pFn.fn_CreateSubChart02 = function(dataObj){				

					subChart02.setJSONData(dataObj);
					setCmmThemOpt(subChart01, "Line", "PER", false, 0,0);
					subChart02.draw();
				};

				//지도를 클릭 했을 때 이벤트
				pFn.mapchart_onmapclick = function ( properties ) { 

					$(".w2mapchart_map .municipality").css("fill","#5185CB");
            		$(".w2mapchart_map .municipality.c"+properties.code).css("fill","#97B6E0");            		

            		// 기존 조회된 시도와 같다면 생략(더블클릭으로 인한 재조회 방지)
            		if(dm_search.get("sigunguKey") != properties.code){

            			// 조회구분 설정 (1:전국, 2:시도, 3:시군구)
						if(dm_search.get("sigunguKey") == '') {
							dm_search.set("gbn01", "2");
						}
						else {
							dm_search.set("gbn01", "3");
						}

						var nameValue = '';		//캡션에 '계' 붙이기

	            		// 전국지도에서 클릭시 시도 시군구 동일 값 설정
	            		if(dm_search.get("gbn01") == '2'){
		            		dm_search.set("sidoKey", 	properties.code);	// 시도코드 설정
		            		dm_search.set("sigunguKey", properties.code);	// 시군구코드 설정

		            		if(properties.name      == '서울') { nameValue = '서울지역'; sb_city.setText('서울'); }
                            else if(properties.name == '부산') { nameValue = '부산지역'; sb_city.setText('부산'); }
                            else if(properties.name == '대구') { nameValue = '대구지역'; sb_city.setText('대구'); }
                            else if(properties.name == '인천') { nameValue = '인천지역'; sb_city.setText('인천'); }
                            else if(properties.name == '광주') { nameValue = '광주지역'; sb_city.setText('광주'); }
                            else if(properties.name == '대전') { nameValue = '대전지역'; sb_city.setText('대전'); }
                            else if(properties.name == '울산') { nameValue = '울산지역'; sb_city.setText('울산'); }
                            else if(properties.name == '세종') { nameValue = '세종지역'; sb_city.setText('세종'); }
                            else if(properties.name == '경기') { nameValue = '경기지역'; sb_city.setText('경기'); }
                            else if(properties.name == '강원') { nameValue = '강원지역'; sb_city.setText('강원'); }
                            else if(properties.name == '충북') { nameValue = '충북지역'; sb_city.setText('충북'); }
                            else if(properties.name == '충남') { nameValue = '충남지역'; sb_city.setText('충남'); }
                            else if(properties.name == '전북') { nameValue = '전북지역'; sb_city.setText('전북'); }
                            else if(properties.name == '전남') { nameValue = '전남지역'; sb_city.setText('전남'); }
                            else if(properties.name == '경북') { nameValue = '경북지역'; sb_city.setText('경북'); }
                            else if(properties.name == '독도') { nameValue = '경북지역'; sb_city.setText('경북'); }
                            else if(properties.name == '경남') { nameValue = '경남지역'; sb_city.setText('경남'); }
                            else if(properties.name == '제주') { nameValue = '제주지역'; sb_city.setText('제주'); }
                            else {  }

		            		dm_search.set("sidoNm", nameValue);		// 시도명 설정
		            		dm_search.set("sigunguNm", nameValue);	// 시군구명 설정
	            		}
	            		else{	// 시군구 지도에서 클릭시 시군구만 설정
	            			dm_search.set("sidoKey", properties.code.slice(0, 2));	// 시도코드 설정
	            			dm_search.set("sigunguKey", properties.code);	// 시군구코드 설정

		            		var temp = dm_search.get("sidoNm").slice(0,2);

		            		//지도클릭으로 조회 후 Select Box 변경 후 다시 지도클릭 조회로 생기는 버그 처리
		            		if(sb_city.getText() != temp){
		            			nameValue = temp + properties.name;
		            			sb_city.setText(temp);
		            			sel_gungu.setText(temp + properties.name);
		            		}
		            		else{
		            			nameValue = sb_city.getText() + properties.name;
		            			sel_gungu.setText(sb_city.getText() + properties.name);

		            		}


		            		nameValue = sb_city.getText() + properties.name; sel_gungu.setText(sb_city.getText() + properties.name);
							nameValue = nameValue.replace("독도","울릉군");
		            		dm_search.set("sigunguNm", nameValue);	// 시군구명 설정
	            		}
	            		// 하위 차트 조회
						pFn.execSubmission("getSubSelect01");
						pFn.execSubmission("getSubSelect02");
					}              	
            	};


            	//지도정보가 변경되기전 이벤트 : 지도를 시,군 단계까지 제한하는 코드. 아래 주석(return false)을 풀면 2단계로 지도가 확장되지 않는다.
            	pFn.mapchart_onbeforemapchange = function ( info ) {
	              	if(info.newMapCode.length >= 5) {
	              		return false; 
	              	}
				};

              	//지도정보가 변경 되었을때 이벤트 : 현재 지도에 대한 코드 정보 저장
              	pFn.mapchart_onaftermapchange = function () {
              		dm_curCity_info.set("curCode",this.getCurrentMap());
              		dm_curCity_info.set("parentCode",this.getParentMap());

              		// 전국지도라면 본청보기 숨기기
              		if(this.getCurrentMap() == 0){
						$("#boneMap").addClass("hidden");		// 본청보기 숨기기
					}
					else{
            			$("#boneMap").removeClass("hidden");	// 본청보기 보이기  		
					}
	            };

	            //본청보기 버튼 클릭 시 이벤트
              	pFn.boneMove = function(){ 
					// 조회구분 설정 (3:시군구, 본청)
            		dm_search.set("gbn01", '3');
           			dm_search.set("sigunguKey", dm_search.get("sidoKey"));			// 본청 코드와 시도 코드가 같음
            		dm_search.set("sigunguNm", sb_city.getText() + "본청");	// 시군구명 설정

					//셀렉트 박스 값 변경
            		sel_gungu.setText(sb_city.getText() + "본청");

            		// 하위 차트 조회
					pFn.execSubmission("getSubSelect01");
					pFn.execSubmission("getSubSelect02");
              	};

	            //상위 지도로 버튼 클릭 시 이벤트
              	pFn.topMapMove = function(){ 
              		var parentCode = dm_curCity_info.get("parentCode");
              		mapchart.changeMap(parentCode);

              		// 조회구분값 설정 and 기본 텍스트 설정
					dm_search.set("gbn01", "1");	
					dm_search.set("sidoKey", "");	
					dm_search.set("sidoNm", "전국");	
					dm_search.set("sigunguKey", "");	
					dm_search.set("sigunguNm", "전국");	

            		//셀렉트 박스 값 변경
					sb_city.setValue('');
					sel_gungu.setValue('');

					// 하위 차트 조회				
					pFn.execSubmission("getSubSelect01");
					pFn.execSubmission("getSubSelect02");
              	};

				// 화면 초기 설정
				pFn.onLoad = function(){

					// 연도 설정
					//pFn.setYear(10);
					pFn.execSubmission("getYear");	
				};

				// 화면 onLoad
				pFn.onLoad();

				pFn.getSubSelect01_submitdone = function(e) {
					var dataObj = {"chart":{},"data":dl_subChart01.getAllJSON()};
					pFn.fn_CreateSubChart01(dataObj);	
				};

				pFn.getSubSelect02_submitdone = function(e) {
					var dataObj = {"chart":{},"data":dl_subChart02.getAllJSON()};
					pFn.fn_CreateSubChart02(dataObj);
				};

				pFn.selectYear_onviewchange = function(info) { 
					dm_search.set("year", selectYear.getValue());

					//맵차트 맵 변경
					setChangeMap("settle", selectYear.getValue());

					// 시도 셀렉트 박스 변경
					pFn.execSubmission("getSido");
				};

				pFn.getYear_submitdone = function(e) {
					dm_search.set("year", selectYear.getValue());

					// 조회구분값 설정 and 기본 텍스트 설정
					dm_search.set("sidoKey", "");	
					dm_search.set("sidoNm", "전국");
					dm_search.set("sigunguKey", "");	
					dm_search.set("sigunguNm", "전국");
					dm_search.set("gbn01", "1");	// 전국 조회 코드값

					// 하위 차트 조회				
					pFn.execSubmission("getSubSelect01");
					pFn.execSubmission("getSubSelect02");
				};

				pFn.addMenuLogCallback = function(){	
					//로그 뻥튀기 유지하기 위해 콜백함수로 다음 로직 위치 변경함 -> 로그 뻥튀기 제거한다면 밑에 로직 개선 전으로 위치 돌려놔야함			
					if(sb_city.getValue() == ''){
						pFn.getYear_submitdone();
						mapchart.changeMap("0");
					}else{
						if(sb_city.getValue() == '32'){
							dm_search.set("gbn01", '2');
		           			dm_search.set("sidoKey", 	"29" );	// 시도코드 설정
		            		dm_search.set("sidoNm", 	'세종지역');	// 시도명 설정
		            		dm_search.set("sigunguKey",	"29");	// 시군구코드 설정
		            		dm_search.set("sigunguNm", 	'세종지역');	// 시군구명 설정
		            		mapchart.changeMap( '29');	
		            		// 하위 차트 조회
							pFn.execSubmission("getSubSelect01");
							pFn.execSubmission("getSubSelect02");
						}else{
							dc_search_map.set('year',selectYear.getValue());
							dc_search_map.set('itemCd','A000');
							dc_search_map.set('kindCd',sel_gungu.getValue());
							if(sel_gungu.getValue()==''){
								dc_search_map.set('kindCd',sb_city.getValue()+"00000");	
							};
							pFn.getMapCodeInfo();
						}
					}
				};

				pFn.btn_search_onclick = function(e) { 
					ajaxLib.AddMenuLog(menuId, pFn.addMenuLogCallback); //로그 뻥튀기 유지하기 위해 콜백함수 적용
				};

		pFn.getMapCode_submitdone = function(e) {

			// 전국지도에서 클릭시 시도 시군구 동일 값 설정
           		if(sel_gungu.getValue() == ''){
           			dm_search.set("gbn01", '2');
           			dm_search.set("sidoKey", 	dm_mapInfo.get('code'));	// 시도코드 설정
            		dm_search.set("sidoNm", 	sb_city.getText());			// 시도명 설정
            		dm_search.set("sigunguKey",dm_mapInfo.get('code'));		// 시군구코드 설정
            		dm_search.set("sigunguNm", 	sb_city.getText() + "지역");		// 시군구명 설정

            		// 하위 차트 조회
					pFn.execSubmission("getSubSelect01");
					pFn.execSubmission("getSubSelect02");
           		}
           		else{	// 시군구 지도에서 클릭시 시군구만 설정
           			dm_search.set("gbn01", '3');
           			dm_search.set("sigunguKey",dm_mapInfo.get('code'));	// 시군구코드 설정
            		dm_search.set("sigunguNm", 	sel_gungu.getText());	// 시군구명 설정

            		// 하위 차트 조회
					pFn.execSubmission("getSubSelect01");
           		}			
           		mapchart.changeMap( dm_mapInfo.get('code').substr(0,2));
				mapchart.changeMap( dm_mapInfo.get('code'));	
		};
