		//코멘트 버튼 실행 함수
		var commTxtBtn = true;
	    pFn.comment_onclick = function() {
	    	if(commTxtBtn) {
            	comm_txt.show();
	    		commTxtBtn = false;
	    	} else {
            	comm_txt.hide();
	    		commTxtBtn = true;
	    	}
       	};

